// Telegraph Database
// -------------------------------------------------- //

module.exports.setup = function(server) {

    var env = global.NODE_ENV || "development";

    server.database = server.database[env];

    var connection = {}
    ,   adapter = server.database.adapter
    ;

    switch(adapter){
        
    case "mongo": case "couch": case "redis":
        connection = require("./adapters/" + adapter).install(server); 
        break;
        
    default: 
        // do nothing
    }

    return connection;

};